	function initCarousel() {
				
		//	Responsive layout, resizing the items
		$('#carousel1').carouFredSel({
			responsive: true,
			onWindowResize: 'debounce',
			width: '100%',
			height:'variable',
			prev: '#prev1',
			next: '#next1',
			auto: false,
			swipe: {
				onTouch : true
			},
			items: {
				width: 220,
				height: 'auto',	//	optionally resize item-height
				visible: {
					min: 1,
					max: 3
				}
			},
			scroll: {
				items:3,
				duration  : 1000   //  The duration of the transition.
			}
		});			
				
		//	Responsive layout, resizing the items
